import Phaser from "phaser";

export default class GameScene extends Phaser.Scene {
  private board: string[][] = [
    ["", "", ""],
    ["", "", ""],
    ["", "", ""]
  ];
  private currentPlayer: string = "X";
  private cells: Phaser.GameObjects.Text[][] = [];
  private placeSound!: Phaser.Sound.BaseSound;

  constructor() {
    super("GameScene");
  }

  create() {
    this.add.image(400, 300, "board");
    this.placeSound = this.sound.add("placeSound");

    // Crear las casillas
    for (let row = 0; row < 3; row++) {
      this.cells[row] = [];
      for (let col = 0; col < 3; col++) {
        let cell = this.add.text(200 + col * 100, 150 + row * 100, "", {
          fontSize: "64px",
          color: "#000"
        }).setInteractive();

        cell.on("pointerdown", () => this.handleCellClick(row, col, cell));
        this.cells[row][col] = cell;
      }
    }
  }

  private handleCellClick(row: number, col: number, cell: Phaser.GameObjects.Text) {
    if (this.board[row][col] === "") {
      this.board[row][col] = this.currentPlayer;
      this.placeSound.play();

      // Usar tween para animar la apariciÃ³n de la letra
      this.tweens.add({
        targets: cell,
        scale: 1.2,
        duration: 200,
        yoyo: true,
        onStart: () => {
          cell.setText(this.currentPlayer);
        }
      });

      if (this.checkWin()) {
        this.scene.start("EndScene", { winner: this.currentPlayer });
      } else {
        this.currentPlayer = this.currentPlayer === "X" ? "O" : "X";
      }
    }
  }

  private checkWin(): boolean {
    const lines = [
      // Filas
      [[0, 0], [0, 1], [0, 2]],
      [[1, 0], [1, 1], [1, 2]],
      [[2, 0], [2, 1], [2, 2]],
      // Columnas
      [[0, 0], [1, 0], [2, 0]],
      [[0, 1], [1, 1], [2, 1]],
      [[0, 2], [1, 2], [2, 2]],
      // Diagonales
      [[0, 0], [1, 1], [2, 2]],
      [[0, 2], [1, 1], [2, 0]]
    ];

    return lines.some(line => {
      const [a, b, c] = line;
      return (
        this.board[a[0]][a[1]] !== "" &&
        this.board[a[0]][a[1]] === this.board[b[0]][b[1]] &&
        this.board[a[0]][a[1]] === this.board[c[0]][c[1]]
      );
    });
  }
}
